clc; clear all; close all; 
load('dataset_som_fdk6_t2.mat')
x = dataset_som_fdk6;

% Create a Self-Organizing Map
dimension1 = 5;
dimension2 = 5;
net = selforgmap([dimension1 dimension2]);

% Train the Network
[net_t1,tr_1] = train(net,x);

% Test the Network
resultados_test1 = net(x);

% View the Network
view(net)

